import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FileText, 
  Users, 
  Calendar, 
  Clock, 
  UserPlus
} from 'lucide-react';
import apiService from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState([]);
  const [recentApplications, setRecentApplications] = useState([]);
  const [upcomingAppointments, setUpcomingAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load dashboard stats
      const statsResponse = await apiService.getDashboardStats();
      const dashboardStats = statsResponse;

      // Transform stats for display
      const statsData = [
        {
          name: 'Total Applications',
          value: dashboardStats.applications?.total_applications || 0,
          change: '', // Can be calculated from historical data if needed
          changeType: 'positive',
          icon: FileText,
          color: 'bg-blue-500'
        },
        {
          name: 'Active Customers',
          value: dashboardStats.customers?.active_customers || dashboardStats.customers?.total_customers || 0,
          change: '',
          changeType: 'positive',
          icon: Users,
          color: 'bg-green-500'
        },
        {
          name: 'Pending Visits',
          value: dashboardStats.pending_visits || dashboardStats.visitors?.scheduled || 0,
          change: '',
          changeType: 'neutral',
          icon: Calendar,
          color: 'bg-yellow-500'
        },
        {
          name: 'Staff Members',
          value: dashboardStats.staff?.total_staff || 0,
          change: '',
          changeType: 'positive',
          icon: UserPlus,
          color: 'bg-purple-500'
        }
      ];
      setStats(statsData);

      // Load recent applications
      const applicationsResponse = await apiService.getApplications({ page: 1, limit: 5 });
      const transformedApplications = (applicationsResponse.applications || []).map(app => ({
        id: `APP-${String(app.id).padStart(3, '0')}`,
        customer: `${app.first_name || ''} ${app.last_name || ''}`.trim() || 'N/A',
        service: app.service_name || 'N/A',
        status: app.status || 'draft',
        date: app.created_at ? new Date(app.created_at).toLocaleDateString() : '',
        statusColor: getStatusColor(app.status)
      }));
      setRecentApplications(transformedApplications);

      // Load upcoming appointments
      const today = new Date();
      const tomorrow = new Date(today);
      tomorrow.setDate(tomorrow.getDate() + 1);
      const appointmentsResponse = await apiService.getAppointments({ 
        page: 1, 
        limit: 5,
        status: 'all',
        date: 'all'
      });

      // Filter and sort upcoming appointments
      const upcoming = (appointmentsResponse.appointments || [])
        .filter(apt => {
          const aptDate = new Date(apt.appointment_date);
          return aptDate >= today && (apt.status === 'pending' || apt.status === 'confirmed');
        })
        .sort((a, b) => {
          const dateA = new Date(`${a.appointment_date}T${a.appointment_time || '00:00:00'}`);
          const dateB = new Date(`${b.appointment_date}T${b.appointment_time || '00:00:00'}`);
          return dateA - dateB;
        })
        .slice(0, 5)
        .map(apt => {
          const aptDate = new Date(apt.appointment_date);
          const isToday = aptDate.toDateString() === today.toDateString();
          const isTomorrow = aptDate.toDateString() === tomorrow.toDateString();

          return {
            id: apt.id,
            customer: apt.customer_name || 'N/A',
            service: apt.service || 'N/A',
            time: apt.appointment_time || 'N/A',
            date: isToday ? 'Today' : isTomorrow ? 'Tomorrow' : aptDate.toLocaleDateString()
          };
        });
      setUpcomingAppointments(upcoming);

    } catch (err) {
      console.error('Error loading dashboard data:', err);
      setError(err.message || 'Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'approved':
        return 'bg-green-100 text-green-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      case 'under_review':
        return 'bg-yellow-100 text-yellow-800';
      case 'submitted':
        return 'bg-blue-100 text-blue-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'draft':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600">Welcome to VisaLink Africa Management System</p>
        </div>
        <div className="card text-center py-12">
          <p className="text-gray-600">Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600">Welcome to VisaLink Africa Management System</p>
        </div>
        <div className="card text-center py-12">
          <p className="text-red-600">Error: {error}</p>
          <button 
            onClick={loadDashboardData}
            className="mt-4 btn-primary"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="text-gray-600">Welcome to VisaLink Africa Management System</p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => (
          <div key={stat.name} className="card">
            <div className="flex items-center">
              <div className={`p-3 rounded-lg ${stat.color}`}>
                <stat.icon className="h-6 w-6 text-white" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">{stat.name}</p>
                <p className="text-2xl font-bold text-gray-900">
                  {typeof stat.value === 'number' ? stat.value.toLocaleString() : stat.value}
                </p>
                {stat.change && (
                  <p className={`text-sm ${stat.changeType === 'positive' ? 'text-green-600' : stat.changeType === 'negative' ? 'text-red-600' : 'text-gray-600'}`}>
                    {stat.change} from last month
                  </p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Applications */}
        <div className="card">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Recent Applications</h2>
            <button 
              onClick={() => navigate('/applications')}
              className="text-primary-600 hover:text-primary-700 text-sm font-medium"
            >
              View all
            </button>
          </div>
          <div className="space-y-3">
            {recentApplications.length === 0 ? (
              <p className="text-center text-gray-500 py-4">No recent applications</p>
            ) : (
              recentApplications.map((app) => (
                <div key={app.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div>
                    <p className="font-medium text-gray-900">{app.id}</p>
                    <p className="text-sm text-gray-600">{app.customer} - {app.service}</p>
                    <p className="text-xs text-gray-500">{app.date}</p>
                  </div>
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${app.statusColor}`}>
                    {app.status?.replace('_', ' ').toUpperCase() || 'DRAFT'}
                  </span>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Upcoming Appointments */}
        <div className="card">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Upcoming Appointments</h2>
            <button 
              onClick={() => navigate('/appointments')}
              className="text-primary-600 hover:text-primary-700 text-sm font-medium"
            >
              View all
            </button>
          </div>
          <div className="space-y-3">
            {upcomingAppointments.map((appointment) => (
              <div key={appointment.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div>
                  <p className="font-medium text-gray-900">{appointment.customer}</p>
                  <p className="text-sm text-gray-600">{appointment.service}</p>
                  <p className="text-xs text-gray-500">{appointment.date}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm font-medium text-gray-900">{appointment.time}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <button 
            onClick={() => navigate('/applications')}
            className="btn-primary flex items-center justify-center space-x-2"
          >
            <FileText className="h-5 w-5" />
            <span>New Application</span>
          </button>
          <button 
            onClick={() => navigate('/customers')}
            className="btn-secondary flex items-center justify-center space-x-2"
          >
            <Users className="h-5 w-5" />
            <span>Add Customer</span>
          </button>
          <button 
            onClick={() => navigate('/appointments')}
            className="btn-outline flex items-center justify-center space-x-2"
          >
            <Clock className="h-5 w-5" />
            <span>Schedule Appointment</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
